import React from 'react';
import './Favoris.scss';
import FileLiknk from '../file-link/File-link';
class Favoris extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            openFavoris : false
          }
    }

    onOpening(){
        this.setState({ 
            openFavoris : true

        })
    }


    render(){
        return (
            <div className="FavorisContainer">
                <button className="btn favoris"onClick= {this.onOpening}> 
                    mes favoris  <i className="icon-arrow_icon"></i>
                </button>

                {this.state.openFavoris === true ? 

                <div className="FavorisWrapper">
                    <FileLink></FileLink>
                </div>
                : null}


            </div>
        );
    }
}

export default Favoris;
