import React from 'react';
import './Top.scss';
import '../style/colors.scss';
import '../style/font.scss';
import {Redirect} from 'react-router-dom';

function Top() {
  setRedirect = () => {
    this.setState({
      redirect :true
    })
}

renderRedirect = ()  =>{
    if (this.state.redirect){
      return <Redirect to='/informations' />
    }
}

  return (
    <div className="Head">

      <div className="brandingWrapper">
        <div className="logo"></div>

        <div className="brand">
          sherpa
        </div>
      </div>

      <div className="buttonWrapper">

        <button className="btn favoris"> 
          mes favoris <i className="icon-arrow_icon"></i>
        </button>

        {/*  @todo:alice calenddrier va devenir une modale */}
        <button className="btn calendar">
          Deconnexion 
        </button> 

      </div>

    </div>
  );
}

export default Top;
