import React from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';
import  './gmap.scss';
import pinMapPath from '../asset/pinMap.png'


export class Gmap extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      stores: [{lat: 48.8473435, lng: 2.2487285},  
        {latitude: 48.8473435, longitude: 2.2487285, name: 'Paris'},
        {latitude: 48.9292771, longitude: 2.245583, name: 'Lyon'}, 
        {latitude: 48.8661091, longitude: 2.3102657, name: 'Paris'}, 
        {latitude: 48.8385379, longitude: 2.3763955, name: 'Paris'}, 
        {latitude: 48.8556475, longitude: 2.2964417, name: 'Paris'}, 
        {latitude: 48.8942046, longitude: 2.3910398, name: 'Paris'}, 
        {latitude: 48.8561073, longitude: 2.3105942, name: 'Paris'}, 
        {latitude: 48.9614725, longitude: 2.4350133, name: 'Paris'}, 
        {latitude: 48.9244592, longitude: 2.3579758, name: 'Paris'}, 
        {latitude: 48.8350921, longitude: 2.2535702, name: 'Paris'}, 
        {latitude: 48.8414356, longitude: 2.25086, name: 'Paris'}, 
        {latitude: 48.832672,  longitude: 2.2843717, name: 'Paris'}, 
        {latitude: 48.8952791, longitude: 2.2272789, name: 'Paris' } 
      ],
      police: [
        {},
        {}
      ] 
    }  
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker 
      id={index}
      position={{ 
        lat: store.latitude,
        lng: store.longitude
      }}
      icon={{
        url: pinMapPath,

      }}
      onClick={() => console.log("You clicked me!")} 

      name={'Current location'} 
      />
    })
  } 



  render() {
    return (

      <Map
      className= "gmapContainer"
      google={this.props.google}
      zoom={12}
      style={mapStyles}
      initialCenter={{ lat: 48.8583701, lng: 2.2944813}} 
      >
      {this.displayMarkers()}
      </Map>


      );
    }
  }

  export default GoogleApiWrapper({
    apiKey: 'AIzaSyC9t3-AiZzfZ7zOWgAChLf7-3jQ8PPPTgY'
  })(Gmap);


