import React from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import  './gmap.scss';
export class Gmap extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        stores: [{lat: 48.8473435, lng: 2.2487285},  
                {latitude: 48.8473435, longitude: 2.2487285},
                {latitude: 48.9292771, longitude: 2.245583}, 
                {latitude: 48.8661091, longitude: 2.3102657}, 
                {latitude: 48.8385379, longitude: 2.3763955}, 
                {latitude: 48.8556475, longitude: 2.2964417}, 
                {latitude: 48.8942046, longitude: 2.3910398}, 
                {latitude: 48.8561073, longitude: 2.3105942}, 
                {latitude: 48.9614725, longitude: 2.4350133}, 
                {latitude: 48.9244592, longitude: 2.3579758}, 
                {latitude: 48.8350921, longitude: 2.2535702}, 
                {latitude: 48.8414356, longitude: 2.25086}, 
                {latitude: 48.832672,  longitude: 2.2843717}, 
                {latitude: 48.8952791, longitude: 2.2272789 } 
            ] 
      }
    }

    displayMarkers = () => {
      return this.state.stores.map((store, index) => {
        return <Marker 
            //key={index} 
            id={index}
            position= {{
            lat: store.latitude,
            lng: store.longitude
            }}
         /*   icon={{
             url: './src/asset/pinMap.png'
           }} */

       onClick={() => console.log("You clicked me!")} 


        />
      })
    }



    render() {
      return (

          <Map
            className= "gmapContainer"
            google={this.props.google}
            zoom={11}
            style={mapStyles}
            initialCenter={{ lat: 48.8583701, lng: 2.2944813}}
          >
            {this.displayMarkers()}
          </Map>

      );
    }
  }

export default GoogleApiWrapper({
 apiKey: 'AIzaSyC9t3-AiZzfZ7zOWgAChLf7-3jQ8PPPTgY'
})(Gmap);


const mapStyles = {
    width: '100%',
    height: '100%',
    //marginTop: '-60px',
    zIndex: '-5',
    overflow: 'hidden',
};
