import React from 'react';
import './Favoris.scss';
import FileLink from '../file-link/File-link';

class Favoris extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            openFavoris : false
        }
        this.openFavoris = this.openFavoris.bind(this)
    }

    fileGenerator(){
        let array = [];
        for ( let i =0 ; i <30; i++){
          array.push(<FileLink/>)
        }
        return array
      }
    onOpening(){
        this.setState({ 
            openFavoris : true

        })
    }



    render(){
        return (
            <div className="FavorisContainer">
                <button className="btn favoris"onClick= {this.onOpening}> 
                    mes favoris  <i className="icon-arrow_icon"></i>
                </button>

                {this.state.openFavoris === true ? 

                <div className="FavorisWrapper">
                    {this.fileGenerator()} 
                </div>

                : null}


            </div>
        );
    }
}

export default Favoris;
